function [A,M,b]=AssembHelmoltz2D(g,N,T,P);%% Assemblierung der Steifigkeitsmatrix A %% der Massmatrix M und des Lastvektors b % g -> Dirichlet-RBn=size(T,1);m=size(N,1);A=sparse(m,m);M=sparse(m,m);bn=zeros(m,1);for i=1:1,  % Assemblierung, die Materiale-Konstanten nicht   % vergessen  Ae=Elementsteifigkeitsmatrix(N,T,i);  Me=P(i)*Elementmassenmatrix(N,T,i);  % die Raender  bn(T(i,1))=bn(T(i,1)) | T(i,4) | T(i,6);      bn(T(i,2))=bn(T(i,2)) | T(i,4) | T(i,5);  bn(T(i,3))=bn(T(i,3)) | T(i,5) | T(i,6);  A(T(i,1:3),T(i,1:3))=A(T(i,1:3),T(i,1:3))+Ae;  M(T(i,1:3),T(i,1:3))=M(T(i,1:3),T(i,1:3))+Me;end;% Aufbau des Lastvektor b=zeros(m,1);                                    for i=1:m,  if bn(i)>0,  % on a un moins devant b car   % apres on divise par un -A  % le probleme est Laplace u+ alpha u=b!!    b(i)=-feval(g,N(i,1),N(i,2));    A(i,:)=zeros(1,m);A(i,i)=1;    M(i,:)=zeros(1,m);  end;end;