function Me=Elementmassenmatrix(N,T,el);%% Berechnet die Elementmassenmatrix fuer das%% Element el Ecken=[N(T(el,1),:); N(T(el,2),:); N(T(el,3),:)];% Koord. der Dreieck elx1=Ecken(1,1); y1=Ecken(1,2);x2=Ecken(2,1); y2=Ecken(2,2);x3=Ecken(3,1); y3=Ecken(3,2);% Formel fuer die Elementmm ... % bei hand ...Jd=-x1*y3-x2*y1+x2*y3+x1*y2+x3*y1-x3*y2;       Me=Jd/24*[2 1 1; 1 2 1; 1 1 2];