function Me=Elementmassenmatrix(N,T,el);%% Berechnet die Elementmassenmatrix fuer das%% Element el % F=[1 N(T(el,1),1) N(T(el,1),2);%    1 N(T(el,2),1) N(T(el,2),2);%    1 N(T(el,3),1) N(T(el,3),2)];J=[N(T(el,2),1)-N(T(el,1),1) N(T(el,3),1)-N(T(el,1),1);   N(T(el,2),2)-N(T(el,1),2) N(T(el,3),2)-N(T(el,1),2)];fl=abs(det(J));Me=[1/12 1/24 1/24;    1/24 1/12 1/24;    1/24 1/24 1/12];Me=fl*Me;