function Ae=Elementsteifigkeitsmatrix(N,T,el);%% Berechnet die Elementsteifigkeitsmatrix fuer das %% Element el% F=[1 N(T(el,1),1) N(T(el,1),2);%     1 N(T(el,2),1) N(T(el,2),2);%     1 N(T(el,3),1) N(T(el,3),2)];% % fl=1/2*abs(det(F));% % Ae=[ 2 -1 -1;%     -1  1  0;%     -1  0  1];% % Ae=fl*Ae;J=[N(T(el,2),1)-N(T(el,1),1) N(T(el,3),1)-N(T(el,1),1);   N(T(el,2),2)-N(T(el,1),2) N(T(el,3),2)-N(T(el,1),2)];D=[-ones(1,2);eye(2)]/J;Ae=1/2*abs(det(J))*D*D';